sys_script_client.script:
function onSubmit() {
    var deviceSysId = g_form.getValue('medical_device');

    alert("deviceSysId : " + deviceSysId);

    var ga = new GlideAjax('hcCheckDeviceName');
    ga.addParam('sysparm_name', 'checkDeviceName');
    ga.addParam('sysparm_sys_id', deviceSysId);

    ga.getXML(function(response) {
		g_form.addInfoMessage('Inside getXML');
        var answer = response.responseXML.documentElement.getAttribute('answer');

        g_form.addInfoMessage("Client script HC Guide usr fr  Heart Rate Discrepancy says answer : " + answer);

        if (answer === "true") {
            if (g_form.getValue('heart_rate_discrepancy_details') === ""){
 				g_form.addInfoMessage("Provide Heart Rate Discrepancy  Details");
                g_form.addInfoMessage("Test3");
                g_form.setAbortAction(true);
            } else {
               g_form.submit();
            }
        } else {
            g_form.submit();
        }

    });



    return true;

}
