sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var urgency = g_form.getValue('urgency');
    var impact = g_form.getValue('impact');

    if (urgency == '3' && impact == '3') {
        g_form.setValue('priority', '5');
    }
    if (urgency == '3' && impact == '2') {
        g_form.setValue('priority', 4);
    }
    if (urgency == '3' && impact == '1') {
        g_form.setValue('priority', 3);
    }
    if (urgency == '2' && impact == '3') {
        g_form.setValue('priority', 4);
    }
    if (urgency == '2' && impact == '2') {
        g_form.setValue('priority', 3);
    }
    if (urgency == '2' && impact == '1') {
        g_form.setValue('priority', 2);
    }
    if (urgency == '1' && impact == '3') {
        g_form.setValue('priority', 3);
    }
    if (urgency == '1' && impact == '2') {
        g_form.setValue('priority', 2);
    }
    if (urgency == '1' && impact == '1') {
        g_form.setValue('priority', 1);
    }

    //Type appropriate comment here, and begin script below

}
