sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var categorySubcategoryMap = {
        "HC Monitoring Equipment": ["--None--","Device Malfunction", "Connection Issues", "Alarm Notifications", "Data Accuracy Concerns","Other"],
        "HC Infusion Device": ["--None--","Flow Rate Irregularities", "Occlusion Alarms", "Battery or Power Issues", "Medication Administration Errors","Other"],
        "HC Imaging Equipment": ["--None--","Image Quality Issues", "Radiation Exposure Concerns", "Mechanical Failures", "Software Errors","Other"],
        "HC Cardiac Equipment": ["--None--","Failure to Deliver Shock", "Electrode Connection Problems", "Battery or Power Issues", "Inaccurate ECG Readings","Other"],
        "HC Respiratory Equipment": ["--None--","Ventilation Settings Issues", "Airflow or Pressure Problems", "Alarms for Oxygen Levels", "Sudden Shutdowns","Other"],
        "HC Diagnostic Equipment": ["--None--","Incorrect Readings", "Calibration Problems", "Battery or Power Issues", "User Interface Errors","Other"],
        "HC Surgical Equipment": ["--None--","Power Fluctuations", "Beam Alignment Problems", "Cooling System Failures", "Safety Interlock Issues","Other"],
        "HC Health Information Technology (HIT)": ["--None--","Login or Access Issues", "Data Entry Errors", "System Downtime", "Integration Problems with Other Systems","Other"],
        "HC Mobility Aids": ["--None--","Joystick or Control Malfunctions", "Battery or Power Issues", "Brake Failures", "Structural Problems","Other"],
        "HC IT Equipment": ["--None--","Hardware Malfunction", "Software Issues", "Connection Problems", "Battery or Power Issues","Other"],

    }

    var selectedCategory = g_form.getValue('incident_category');

    // alert(selectedCategory);

    g_form.clearOptions('subcategory');

    // alert(JSON.stringify(categorySubcategoryMap[selectedCategory]));

	if(selectedCategory && categorySubcategoryMap[selectedCategory]){
		categorySubcategoryMap[selectedCategory].forEach(function(subcategory){
			g_form.addOption('subcategory',subcategory,subcategory);
		});
	}

}
