sys_script_client.script:
function onSubmit() {

    var deviceCategory = g_form.getValue('incident_category');

	if(deviceCategory=="HC Imaging Equipment"){
		if(g_form.getValue("imaging_equipment_issue_details")==""){
		g_form.setVisible("imaging_equipment_issue_details",true);
		g_form.setMandatory("imaging_equipment_issue_details",true)
		g_form.addErrorMessage("Provide Details of Imaging Equipment Issues");
		return false;
		}else{
			return true;
		}
	}else{
		g_form.setVisible("imaging_equipment_issue_details",false);
		g_form.setMandatory("imaging_equipment_issue_details",false);
	}

	return true;



}
