sys_script_client.script:
function onSubmit() {
    //Type appropriate comment here, and begin script below

    var subcategory = g_form.getValue('subcategory');
// alert("Subcategory : " + subcategory);
    if (subcategory == "Battery or Power Issues") {
// alert("test1");
        var batteryIssueDetails = g_form.getValue('battery_issue_details');
// alert("battery issue details : " + batteryIssueDetails);
        if (batteryIssueDetails == "") {
// alert("Test2");
			g_form.addErrorMessage("Please provide additional information about Battery or Power related issues.");
			return false;
		}else{
			additionalValidations()
		}
    }

	return true;
}

function additionalValidations(){
	var ga=new GlideAjax('hcValidateBatteryIssueDetails');

	ga.addParam('sysparm_name','validateBatteryIssueDetails');
	ga.addParam('sysparm_battery_issue_details',g_form.getValue('battery_issue_details'));

	ga.getXML(function(response){
		var answer=response.responseXML.documentElement.getAttribute('answer');
// alert("Test3 : "+answer);

	if(answer!=true){
		g_form.addErrorMessage("Minimum 5 characters are requiered in Barrery Issue Details field.");
	}

	});

}
