sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }


    g_form.getReference('medical_device', function(medDevice) {

        if (medDevice) {
            // g_form.addInfoMessage("medDevice : "+JSON.stringify(medDevice));
            var deviceNumber = medDevice.getValue('number');
            var deviceName = medDevice.getValue('device_name');
            var deviceCategory = medDevice.getValue('device_type');

            // g_form.addInfoMessage("deviceName : " + deviceName + " <==> deviceCategory : " + deviceCategory + " <==> deviceNumber : " + deviceNumber);
        }

        g_form.setValue('incident_category', deviceCategory);

    });




}
