sys_script_include.script:
var hcCheckDeviceName = Class.create();
hcCheckDeviceName.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	checkDeviceName : function(){
		var deviceSysId=this.getParameter('sysparm_sys_id');

		var gr=new GlideRecord('x_1132366_healthca_medical_devices');
		gr.addQuery('sys_id',deviceSysId);
		gr.query();

		if(gr.next()){
			var deviceName=gr.getValue('device_name');
			gs.addInfoMessage("Script include hcCheckDeviceName says device name selected is : "+deviceName);
			if(deviceName=="HC Patient Monitoring Device"){
				gs.addInfoMessage('Test1');
				return true;
			}else{
					gs.addInfoMessage('Test2');
				return false;
			}



		}else{
			gs.addErrorMessage("Record not found");
		}

	},

    type: 'hcCheckDeviceName'
});
