sys_script_include.script:
var hcSendEmailDemo = Class.create();
hcSendEmailDemo.prototype = {
    initialize: function() {
    },

	sendEmail: function(receiverId){
		// gs.addInfoMessage("This info msg is from script include : hcSendEmailDemo");
		// gs.info("This info msg is from script include : hcSendEmailDemo");
		// gs.addInfoMessage("receiver : "+receiverId);
		// gs.info("receiver : "+receiverId);


		if(receiverId){
			var gr=new GlideRecord('sys_user');

			gr.addQuery('sys_id',receiverId);
			gr.query();

			if(gr.next()){
				try{
var receiverEmail=gr.getValue('email');

				// gs.addInfoMessage("Email : "+receiverEmail);

				var email = new GlideEmailOutbound();
				email.setFrom("ketan@example.com");
				email.addTo(receiverEmail);

				email.setSubject("Demo email subject");
				email.setBody("Demo email body");
				email.send();
				gs.addInfoMessage("Email Sent successfully");

				}catch(e){

					// gs.addErrorMessage("Cant send an email : Error -"+e);
				}


			}else{
				// gs.addErrorMessage("User not found. Cant send email");
			}




		}

	},

    type: 'hcSendEmailDemo'
};
