sys_script_include.script:
var hcCheckMonitoringDevice = Class.create();
hcCheckMonitoringDevice.prototype = {
    initialize: function() {
    },

	checkDevice:function(deviceSysId){


		var gr= new GlideRecord('x_1132366_healthca_medical_devices');
		gr.addQuery('sys_id',deviceSysId);
		gr.query();
		if(gr.next()){
			var deviceName=gr.getValue("device_name");
			// gs.addInfoMessage("Test222 : "+deviceName);
			if(deviceName==="HC Patient Monitoring Device"){
				return true;
			}else{
				return false;
			}
		}



	},

    type: 'hcCheckMonitoringDevice'
};
